mapper.select:
select
  id, stage, stage_date, stage_num, stage_index, open_number, pre3_number, pre2_number, number1, number2, number3, number4, number5, curr_continued_in_count, curr_continued_out_count, curr_continued_inclined_count, create_time, update_time
from yjcycc_lottery.bs_open_number


        and id = #{id}


        and stage = #{stage}


        and stage_date = #{stageDate}


        and stage_num = #{stageNum}


        and stage_index = #{stageIndex}


        and open_number = #{openNumber}


        and pre3_number = #{pre3Number}


        and pre2_number = #{pre2Number}


        and number1 = #{number1}


        and number2 = #{number2}


        and number3 = #{number3}


        and number4 = #{number4}


        and number5 = #{number5}


        and curr_continued_in_count = #{currContinuedInCount}


        and curr_continued_out_count = #{currContinuedOutCount}


        and curr_continued_inclined_count = #{currContinuedInclinedCount}


        and create_time = #{createTime}


        and update_time = #{updateTime}

