mapper.select:
select
  id, plan_id, play_category_id, lottery_type, status, dict_amount_model, order_no, stage, stage_date, stage_num, stage_index, number, amount, win_amount, win_count, double_count, create_time, update_time
from yjcycc_lottery.bs_order


        and id = #{id}


        and plan_id = #{planId}


        and play_category_id = #{playCategoryId}


        and lottery_type = #{lotteryType}


        and status = #{status}


        and dict_amount_model = #{dictAmountModel}


        and order_no = #{orderNo}


        and stage = #{stage}


        and stage_date = #{stageDate}


        and stage_num = #{stageNum}


        and stage_index = #{stageIndex}


        and number = #{number}


        and amount = #{amount}


        and win_amount = #{winAmount}


        and win_count = #{winCount}


        and double_count = #{doubleCount}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit 1

