mapper.select:
select


from yjcycc_lottery.bs_plan_config


        and id = #{id}


        and dict_amount_model = #{dictAmountModel}


        and dict_double_scheme = #{dictDoubleScheme}


        and dict_lottery_type like concat('%',#{dictLotteryType},'%')


        and double_count = #{doubleCount}


        and dict_choose_scheme = #{dictChooseScheme}


        and dan_number = #{danNumber}


        and tuo_number = #{tuoNumber}


        and is_occupy = #{isOccupy}


        and is_occupy in (${inIsOccupy})


        and cash_out_rate = #{cashOutRate}


        and execute_index = #{executeIndex}


        and recharge = #{recharge}


        and balance = #{balance}


        and total_profit_amount = #{totalProfitAmount}


limit 1

