mapper.select:
select
  id, play_category_id, dict_double_scheme, dict_number_scheme, dict_pursue_scheme, dict_amount_model, is_occupy, create_time, update_time
from yjcycc_lottery.bs_plan_config


        and id = #{id}


        and play_category_id = #{playCategoryId}


        and dict_double_scheme = #{dictDoubleScheme}


        and dict_number_scheme = #{dictNumberScheme}


        and dict_pursue_scheme = #{dictPursueScheme}


        and dict_amount_model = #{dictAmountModel}


        and is_occupy = #{isOccupy}


        and create_time = #{createTime}


        and update_time = #{updateTime}

