mapper.select:
select
  id, plan_config_id, status, total_amount, create_time, update_time
from yjcycc_lottery.bs_plan


        and id = #{id}


        and plan_config_id = #{planConfigId}


        and status = #{status}


        and total_amount = #{totalAmount}


        and create_time = #{createTime}


        and update_time = #{updateTime}

