mapper.select:
select


from yjcycc_lottery.bs_plan


        and id = #{id}


        and plan_config_id = #{planConfigId}


        and dict_lottery_type = #{dictLotteryType}


        and status = #{status}


        and stage_count = #{stageCount}


        and total_stage_count = #{totalStageCount}


        and amount = #{amount}


        and double_count = #{doubleCount}


        and total_amount = #{totalAmount}


        and win_amount = #{winAmount}


        and profit_amount = #{profitAmount}


        and execute_index = #{executeIndex}


limit 1

