mapper.select:
select
  id, plan_config_id, status, amount, double_count, stage_count, total_stage_count, total_amount, win_amount, profit_amount, create_time, update_time
from yjcycc_lottery.bs_plan


        and id = #{id}


        and plan_config_id = #{planConfigId}


        and status = #{status}


        and amount = #{amount}


        and double_count = #{doubleCount}


        and stage_count = #{stageCount}


        and total_stage_count = #{totalStageCount}


        and total_amount = #{totalAmount}


        and win_amount = #{winAmount}


        and profit_amount = #{profitAmount}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit 1

