mapper.select:
select
  id, name, code, single_count, single_amount, create_time, update_time
from yjcycc_lottery.bs_play_category


        and id = #{id}


        and name = #{name}


        and code = #{code}


        and single_count = #{singleCount}


        and single_amount = #{singleAmount}


        and create_time = #{createTime}


        and update_time = #{updateTime}

