mapper.select:
select
  id, plan_config_id, play_category_id, dict_pursue_scheme, stage_count_start, stage_count_end, stage_count, sort, create_time, update_time
from yjcycc_lottery.bs_pursue_scheme


        and id = #{id}


        and plan_config_id = #{planConfigId}


        and play_category_id = #{playCategoryId}


        and dict_pursue_scheme = #{dictPursueScheme}


        and stage_count_start = #{stageCountStart}


        and stage_count_end = #{stageCountEnd}


        and stage_count = #{stageCount}


        and sort = #{sort}


limit 1

