mapper.select:
select
  id, label, value, code, type, description, create_time, update_time
from yjcycc_lottery.sys_dict


        and id = #{id}


        and label = #{label}


        and value = #{value}


        and code = #{code}


        and type = #{type}


        and description = #{description}

