mapper.select:
select
  id, username, password, balance, create_time, update_time
from yjcycc_lottery.bs_user_balance


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and balance = #{balance}


        and create_time = #{createTime}


        and update_time = #{updateTime}

